mapper.select:
SELECT c.contribution_id, c.member_id, m.member_name, c.project_id, p.project_name, c.contribution_type, c.content, c.url, c.contribution_time, c.points, c.create_time, c.update_time FROM os_contribution c LEFT JOIN os_member m ON c.member_id = m.member_id LEFT JOIN os_project p ON c.project_id = p.project_id
AND c.member_id = #{memberId}
AND m.member_name LIKE CONCAT('%', #{memberName}, '%')
AND c.project_id = #{projectId}
AND p.project_name LIKE CONCAT('%', #{projectName}, '%')
AND c.contribution_type = #{contributionType}
AND c.content LIKE CONCAT('%', #{content}, '%')
AND c.contribution_time >= #{params.beginTime}
AND c.contribution_time <= #{params.endTime}
ORDER BY c.contribution_time DESC
